const http = require('http')

// gets the query and retuns the list of data from the query
export async function getList(reqURL, query)
{
	var list = [];

	// post request
	var req = reqURL+encodeURIComponent(query)
	await Promise.resolve(http.get(req, (resp) => {
		let data = '';
		
		// A chunk of data has been received.
		resp.on('data', (chunk) => {
			data += chunk;
		});
		
		// The whole response has been received. Print out the result.
		resp
		.on('end', () => {
			// extract the dataset names from html
			for (let i = 0; i < data.split('<literal>').length; i++)
			{
				list[i] = data.split('<literal>')[i].split('</literal>')[0];
			}
			list.shift();
			list.sort((a, b) => a.localeCompare(b, undefined, {sensitivity: 'base'})); // sort
			});
		})
		.on("error", (err) => {
			console.log(err)
		}));
	

	//await fetch(req).then(data => console.log(data));
		console.log(list);
	console.log(await Promise.resolve(list));
	return await Promise.resolve(list);
}
