import { createTheme } from "@material-ui/core";

const darkTheme = createTheme({
    palette: {
        primary: {
            main: '#082032'
        },
        secondary: {
            main: '#FF4C29'
        },
        background: {
            default: "#2C394B"
          }
    } 
    
});

const lightTheme = createTheme({
    palette: {
        primary: {
        main: '#FEA82F'
        },
        secondary: {
        main: '#FF6701'
        }
    }  
});

export function toggleTheme(mode)
{
    var theme = lightTheme;
    mode === 'light' ? theme = lightTheme : theme = darkTheme;
    return theme
}
