import { createTheme, styled } from "@mui/material/styles";
import AddIcon from '@mui/icons-material/Add';
import {Switch, Card, Autocomplete, Paper, Button } from "@mui/material";

// 0: header   1: body  2: pannels  3: highlight  4: text    
const darkThemeColors = ['#082032', '#2C394B', '#7c848f', '#FB9300', '#fff'];
const lightThemeColors = [];

export const darkTheme = createTheme({
    palette: {
        //mode: 'dark',
        primary: {
            main: darkThemeColors[0]
        },
        secondary: {
            main: darkThemeColors[3]
        },
        text: {
            primary: darkThemeColors[4]
        },
        error: {
          main: '#8c0000'
        },
    },
    typography: {
        body1: {
            color: darkThemeColors[4]
        },
        button: {
          color: darkThemeColors[4]
    }
      },
});

export const lightTheme = createTheme({
    palette: {
        //mode: 'light',
        primary: {
            main: '#343F56'
        },
        secondary: {
            main: darkThemeColors[3]
        },
        text: {
            primary: '#000'
        }
    },
    typography: {
        body1: {
             color: '#000'
        },
        button: {
              color: '#000'
        }
      },
});

var currentTheme
export function toggleTheme(theme){
    currentTheme = theme;
    if (theme === 'dark')
    {
        document.body.style.backgroundColor = darkThemeColors[1];

        return {...darkTheme}
    }
    else
    {
        document.body.style.backgroundColor = '#F5E6CA';

        return {...lightTheme}
    }
}

// card
export const CustomCard = styled(Card)(({}) => ({
    backgroundColor: currentTheme === 'dark' ? darkThemeColors[2] : '#f9f0df',
}));

// add icon
export const CustomAddIcon = styled(AddIcon)(({}) => ({
  color: currentTheme === 'dark' ? darkThemeColors[4] : '#000',
}));

// autocomplete 
export const CustomAutocomplete = styled(Autocomplete)(({}) => ({
  '.MuiInputBase-input':{
    color: darkThemeColors[4]
  },
  '.MuiAutocomplete-tag': {
    color: darkThemeColors[4],
    backgroundColor: darkThemeColors[2],
    '.MuiChip-deleteIcon': {
      color: darkThemeColors[1],
      '&:hover': {
        color: "#FB9300",
     },
    },
  },
  '.MuiAutocomplete-inputRoot': {
    color: darkThemeColors[1],
    backgroundColor: darkThemeColors[1],
  },
  '.Mui-focused': {
    backgroundColor: darkThemeColors[1],
    borderRadius: 20 / 2,
  },
  '.MuiAutocomplete-hasClearIcon': {
    color: 'blue',
    backgroundColor: 'blue',
  },
  '.MuiAutocomplete-clearIndicator': {
    color: darkThemeColors[1],
    backgroundColor: darkThemeColors[2],
    '&:hover': {
      background: "#FB9300",
   },
  },
  '.MuiAutocomplete-popupIndicatorOpen': {
    color: darkThemeColors[1],
    backgroundColor: darkThemeColors[2],
    '&:hover': {
      background: "#FB9300",
   },
  },
  '.MuiAutocomplete-popupIndicator': {
    color: darkThemeColors[1],
    backgroundColor: darkThemeColors[2],
    '&:hover': {
      background: "#FB9300",
   },
  },
}));

// for autocomplete dropdown list
export const CustomPaper = (props) => {
  return <Paper style={{background: darkThemeColors[1], color: darkThemeColors[4]}} {...props} />;
};

// delete button
export const CustomDeleteButton = styled(Button)(({}) => ({
  backgroundColor: darkThemeColors[1],
  '&:hover': {
    background: "#FB9300",
 },
}));

// light / dark theme switch
export const MaterialUISwitch = styled(Switch)(({ theme }) => ({
	width: 62,
	height: 34,
	padding: 7,
	'& .MuiSwitch-switchBase': {
	  margin: 1,
	  padding: 0,
	  transform: 'translateX(6px)',
	  '&.Mui-checked': {
		color: darkThemeColors[4],
		transform: 'translateX(22px)',
		'& .MuiSwitch-thumb:before': {
		  backgroundImage: `url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" height="20" width="20" viewBox="0 0 20 20"><path fill="${encodeURIComponent(
			darkThemeColors[4],
		  )}" d="M4.2 2.5l-.7 1.8-1.8.7 1.8.7.7 1.8.6-1.8L6.7 5l-1.9-.7-.6-1.8zm15 8.3a6.7 6.7 0 11-6.6-6.6 5.8 5.8 0 006.6 6.6z"/></svg>')`,
		},
		'& + .MuiSwitch-track': {
		  opacity: 1,
		  backgroundColor: theme.palette.mode === 'dark' ? '#8796A5' : '#aab4be',
		},
	  },
	},
	'& .MuiSwitch-thumb': {
	  backgroundColor: theme.palette.mode === 'dark' ? '#003892' : darkThemeColors[3],
	  width: 32,
	  height: 32,
	  '&:before': {
		content: "''",
		position: 'absolute',
		width: '100%',
		height: '100%',
		left: 0,
		top: 0,
		backgroundRepeat: 'no-repeat',
		backgroundPosition: 'center',
		backgroundImage: `url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" height="20" width="20" viewBox="0 0 20 20"><path fill="${encodeURIComponent(
		  darkThemeColors[4],
		)}" d="M9.305 1.667V3.75h1.389V1.667h-1.39zm-4.707 1.95l-.982.982L5.09 6.072l.982-.982-1.473-1.473zm10.802 0L13.927 5.09l.982.982 1.473-1.473-.982-.982zM10 5.139a4.872 4.872 0 00-4.862 4.86A4.872 4.872 0 0010 14.862 4.872 4.872 0 0014.86 10 4.872 4.872 0 0010 5.139zm0 1.389A3.462 3.462 0 0113.471 10a3.462 3.462 0 01-3.473 3.472A3.462 3.462 0 016.527 10 3.462 3.462 0 0110 6.528zM1.665 9.305v1.39h2.083v-1.39H1.666zm14.583 0v1.39h2.084v-1.39h-2.084zM5.09 13.928L3.616 15.4l.982.982 1.473-1.473-.982-.982zm9.82 0l-.982.982 1.473 1.473.982-.982-1.473-1.473zM9.305 16.25v2.083h1.389V16.25h-1.39z"/></svg>')`,
	  },
	},
	'& .MuiSwitch-track': {
	  opacity: 1,
	  backgroundColor: theme.palette.mode === 'dark' ? '#8796A5' : '#aab4be',
	  borderRadius: 20 / 2,
	},
  }));

//other switches
export const AntSwitch = styled(Switch)(({ theme }) => ({
    width: 42,
    height: 26,
    padding: 0,
    '& .MuiSwitch-switchBase': {
      padding: 0,
      margin: 4,
      transitionDuration: '300ms',
      '&.Mui-checked': {
        transform: 'translateX(16px)',
        color: darkThemeColors[4],
        '& + .MuiSwitch-track': {
          backgroundColor: theme.palette.mode === 'dark' ? darkThemeColors[3] : darkThemeColors[3],
          opacity: 1,
          border: 0,
        },
        '&.Mui-disabled + .MuiSwitch-track': {
          opacity: 0.5,
        },
      },
      '&.Mui-focusVisible .MuiSwitch-thumb': {
        color: '#8796A5',
        border: '6px solid #fff',
      },
    },
    '& .MuiSwitch-thumb': {
      boxSizing: 'border-box',
      width: 18,
      height: 18,
    },
    '& .MuiSwitch-track': {
      borderRadius: 26 / 2,
      backgroundColor: theme.palette.mode === 'light' ? '#8796A5' : '#8796A5',
      opacity: 1,
      transition: theme.transitions.create(['background-color'], {
        duration: 500,
      }),
    },
  }));
