import React from 'react';
// import { Datasets, Methods} from './dataOptions';
import EvaluationMeasureRange from './EvaluationMeasureRange';
import { Autocomplete } from '@mui/material';
import { Button, TextField } from '@material-ui/core';
import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';
import { Grid } from '@material-ui/core';
//import { Methods } from './dataOptions';
import { DataGrid } from '@material-ui/data-grid'; //npm install @material-ui/data-grid

const http = require('http')

// gets the query and retuns the list of data from the query
export function getList(query, type)
{
	var list = [];

	// post request
	var req = "http://semanticannotations.ijs.si:8890/sparql?default-graph-uri=http%3A%2F%2Flocalhost%3A8890%2FMLC&&Content-Type='application/json'&query="+encodeURIComponent(query)
	http.get(req, (resp) => {
		let data = '';
		
		// A chunk of data has been received.
		resp.on('data', (chunk) => {
			data += chunk;
		});
		
		// The whole response has been received. Print out the result.
		resp
		.on('end', () => {
			// extract the dataset names from html
			for (let i = 0; i < data.split('<literal>').length; i++)
			{
				if (type === "datasets")
				{
					list[i] = data.split('<literal>')[i].split('.arff')[0];
				}
				else
				{
					list[i] = data.split('<literal>')[i].split('</literal>')[0];
				}
			}
			list.shift();
			list.sort((a, b) => a.localeCompare(b, undefined, {sensitivity: 'base'}));

			});
		})
		.on("error", (err) => {
			console.log(err)
		});

	return list;
}

function getRows(query)
{
	var list = [];

	// post request
	var req = "http://semanticannotations.ijs.si:8890/sparql?default-graph-uri=http%3A%2F%2Flocalhost%3A8890%2FMLC&&Content-Type='application/json'&query="+encodeURIComponent(query)
	http.get(req, (resp) => {
		let data = '';
		
		// A chunk of data has been received.
		resp.on('data', (chunk) => {
			data += chunk;
		});
		
		// The whole response has been received. Print out the result.
		resp
		.on('end', () => {
			// extract the dataset names from html
				for (let i = 1; i < data.split('<result>').length; i++)
				{
					var subList = {
						id: data.split('<result>')[i].split('<uri>')[1].split('</uri>')[0],
						firstName: data.split('<result>')[i].split('<uri>')[2].split('</uri>')[0],
						lastName: data.split('<result>')[i].split('<uri>')[3].split('</uri>')[0]
					};
					list.push(subList);
				}
				
				okPLS = list;
				console.log(okPLS);
			});
		})
		.on("error", (err) => {
			console.log(err)
		});

	return list;
}

class Body extends React.Component {

	constructor(props) {
    	super(props);
		this.parentHandleMeasure = this.parentHandleMeasure.bind(this);
		this.parentHandleRange = this.parentHandleRange.bind(this);
		this.handleAdd = this.handleAdd.bind(this)
		this.handleRemove = this.handleRemove.bind(this)

		this.state = {
			evaluationHTMLElements: [],
      		selectedEvals: [
				{
					"measure": "",
					"range": "",
					"id": 0
				}
			],
			addButtonDisabled: true,
			disabledMeasures: [],
			datasetList: [], 
			algorithmList: [],
			foldsAutocompleteDisplayMode: "none",
			spreadsheetData: [],
			spreadSheetColumns: [],
			spreadSheetRows: []
    	}
  	}

	componentDidMount(){
		this.setEvaluationHTMLElements()
		this.getDatasets()
		this.getAlgorithms()
	}

	// gets the names of the datasets
	getDatasets=()=>{
		var query = `
			PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
			PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

			select ?dataset_label
			where {
			?dataset rdf:type <http://www.ontodm.com/OntoDM-core/OntoDM_000144> .
			?dataset rdfs:label ?dataset_label .
			FILTER regex(?dataset_label, "^[^_]+$") .
			}
		`

		this.setState({
			datasetList: getList(query, "datasets")
		})
	}

	// gets the names of the algorithms/methods
	getAlgorithms=()=>{
		var query = `
			PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>

			select distinct ?algorithm_name
			where {
			?dataset rdf:type <http://www.ontodm.com/OntoDM-core/OntoDM_000175> .
			?dataset <http://www.ontodm.com/OntoDM-core/ontoexp_0074> ?algorithm_name.
			}
		`

		this.setState({
			algorithmList: getList(query, "algorithms")
		})
	}

	getDataFromQuery=()=>{
		var query = `
		select *
		where {
		?s ?p ?o
		}
		limit 100
		`

		const columns = [
			{ field: 'id', headerName: 'S', width: 200 },
			{ field: 'firstName', headerName: 'P', width: 200 },
			{ field: 'lastName', headerName: 'O', width: 200 },
		  ];


		const rows = (getRows(query));

		this.setState({
			spreadSheetRows: rows,
			spreadSheetColumns: columns
		})
	}

	parentHandleMeasure(val, id) {
		let newSelectedEvals = []
		this.state.selectedEvals.forEach(el => {
			if(el.id === id){
				el.measure = val
			}
			newSelectedEvals.push(el)
		})
		
		this.setState({
			selectedEvals: newSelectedEvals 
		})
		this.setDisabledMeasures()
		this.addButtonDisabled()	
	}

	parentHandleRange(val, id) {
		let newSelectedEvals = []
		this.state.selectedEvals.forEach(el => {
			if(el.id === id){
				el.range = val
			}
			newSelectedEvals.push(el)
		})
		this.setState({
			selectedEvals: newSelectedEvals 
		})
		this.setDisabledMeasures()
		this.addButtonDisabled()
	}
	

	setEvaluationHTMLElements(){
		console.log("set HTML")
		console.log(this.state.selectedEvals)
		const evals=[]
		for(let i = 0; i<this.state.selectedEvals.length; i++){
			evals.push(
				<Grid container spacing = {2}>
					<Grid item>
						<EvaluationMeasureRange
							onHandleMeasureChange={this.parentHandleMeasure}
							id = {this.state.selectedEvals[i].id}
							range = {this.state.selectedEvals[i].range}
							measure = {this.state.selectedEvals[i].measure}
							onHandleRangeChange={this.parentHandleRange}
							disabledMeasures={this.state.disabledMeasures}
						/>
					</Grid>
					<Grid item>
						<Button
							variant='contained'
							color='secondary'
							onClick={() => this.handleRemove(this.state.selectedEvals[i].id)}
							disabled={this.state.selectedEvals.length === 1}
						>
							<DeleteIcon/>
						</Button>
					</Grid>
				</Grid>
			)
		}
		this.setState({
			evaluationHTMLElements: evals
		})
		console.log(evals)
		this.setDisabledMeasures()
	}

	setDisabledMeasures(){
		const newDisabledMeasures = []
		for(let i = 0; i<this.state.selectedEvals.length; i++){
			newDisabledMeasures.push(this.state.selectedEvals[i].measure)
		}
		this.setState({disabledMeasures: newDisabledMeasures})
	}

	addButtonDisabled(){
		const regex = new RegExp(/^(\d+(\.\d)?\d*(-\d+(\.\d)?\d*)?|[><]=?\d+(\.\d)?\d*)$/)
		for(let i = 0; i<this.state.selectedEvals.length; i++){
			if(this.state.selectedEvals[i].measure===null ||
				this.state.selectedEvals[i].measure==='' ||
				this.state.selectedEvals.length>10 ||
				this.state.selectedEvals[i].range==='' ||
				!regex.test(this.state.selectedEvals[i].range)){
				this.setState({addButtonDisabled: true})
			}
			else{
				this.setState({addButtonDisabled: false})
			}
		}
	}
	handleAdd() {
		let newSelectedEvals = this.state.selectedEvals
			newSelectedEvals.push({
				"measure": "",
				"range": "",
				"id": this.state.selectedEvals[this.state.selectedEvals.length-1].id+1
			})

		this.setState({
			selectedEvals: newSelectedEvals,
			addButtonDisabled: true
		}, 
		()=>{
			console.log("handle add")
			console.log(this.state.selectedEvals)
			this.setEvaluationHTMLElements()
			this.addButtonDisabled()
			this.setDisabledMeasures()
		})
		
	}

	handleRemove(x) {
		console.log("handle remove "+x)
		let newSelectedEvals = []
		for(let i = 0; i<this.state.selectedEvals.length; i++){
			if(this.state.selectedEvals[i].id!==x)
				newSelectedEvals.push(this.state.selectedEvals[i])
		}
		this.setState({
			selectedEvals: newSelectedEvals
		}, 
		()=>{
			console.log("handle remove")
			console.log(this.state.selectedEvals)
			this.setEvaluationHTMLElements()
			this.addButtonDisabled()
			this.setDisabledMeasures()
		})
	}

	render() {
    	return (
			<React.Fragment>

				<Grid container spacing = {0}>
					<Autocomplete // Dataset input field
						multiple = {true}							
						limitTags={50}
						options={this.state.datasetList}
						sx={{width: 300}}
						renderInput={(params) => 
							<TextField {...params} variant='outlined' label = {"Datasets"} color='secondary' />
						}
					/>
					<Autocomplete // split input field
						defaultValue = "train / test"
					 	onChange={(event, value) => {
							if (value === "folds")
							{
								this.setState({foldsAutocompleteDisplayMode : ""});
							}
							else
							{
								this.setState({foldsAutocompleteDisplayMode : "none"});
							}
							}
						}
						multiple = {false}								
						limitTags={2}
						options={["folds", "train / test"]}
						sx={{width: 300}}
						renderInput={(params) => 
						<TextField {...params} variant='outlined' label = "Validation" color='secondary' />
						}
					/>
				</Grid>

				<Autocomplete // methods input field
					multiple = {true}
					limitTags={3}
					options={this.state.algorithmList}
					sx={{width: 300}}
					renderInput={(params) => 
						<TextField {...params} variant='outlined' label = "Methods" color='secondary'/>
					}
				/>

				<Autocomplete // folds input field
					multiple = {true}
					limitTags={50}
					options={["1", "2", "3"]}
					sx={{width: 300, display : this.state.foldsAutocompleteDisplayMode}}
					renderInput={(params) => 
						<TextField {...params} variant='outlined' label = "Fold" color='secondary'/>
					}	
				/>
				{this.state.evaluationHTMLElements}	

				<Button variant='contained'
				color='secondary'
				onClick={this.handleAdd}
				disabled={false} // before: this.state.addButtonDisabled
				>
					<AddIcon />
				</Button>

				<Button // filter / sumbmit button
				color='secondary'
				variant='contained'
				
				onClick={this.getDataFromQuery}
				disabled={false} // before: this.state.addButtonDisabled
				>
					FILTER
				</Button>
				
				<div style={{ height: 400, width: '100%' }}>
				<DataGrid
					rows={okPLS}
					columns={this.state.spreadSheetColumns}
					pageSize={10}
					rowsPerPageOptions={[10]}
				/>
				</div>
		</React.Fragment>
		);
  	}
}

var okPLS = [];

export default Body;
