					console.log('<')
				}
				else if(this.state.selectedEvaluationMeasures[i]['range'].match(/^\d+$/))
				{
					console.log('=')
				}
				else if(this.state.selectedEvaluationMeasures[i]['range'].match(/^(\d+-)+\d+$/))
				{
					console.log('-')
