import React, { useState } from 'react'
import './App.css';
import Header from './components/Header';
import BenchmarkDataFilter from './components/BenchmarkDataFilter'
import { ThemeProvider } from '@material-ui/core/styles';
import { lightTheme, darkTheme, toggleTheme } from './components/themes.js';
import { Button, CssBaseline, GlobalStyles } from "@mui/material";
import { Paper } from '@material-ui/core';

class App extends React.Component {
  constructor(props){
  super(props);

  this.state = {
    selectedTheme: darkTheme,
    }
  }

  setTheme = (theme) =>{
    if (theme === 'light')
    {
      this.setState({selectedTheme: {...lightTheme}})
      document.body.style = 'background: red;';
    }
    else
    {
      this.setState({selectedTheme: {...darkTheme}})
    }
  }

  render() {
    return (
      <React.Fragment>
        <ThemeProvider theme={ this.state.selectedTheme }>

              <Header callback = {this.setTheme}/>
              <BenchmarkDataFilter />    

        </ThemeProvider>
      </React.Fragment>
    );
  }
}

export default App;
