import React, { useState } from 'react'
import './App.css';
import Header from './components/Header';
import BenchmarkDataFilter from './components/BenchmarkDataFilter'
import { ThemeProvider } from '@material-ui/core/styles';
import { lightTheme, darkTheme, toggleTheme } from './components/themes.js';

class App extends React.Component {
  constructor(props){
  super(props);

  this.state = {
    selectedTheme: darkTheme,
    }
  }

  getTheme = (theme) =>{
    theme === 'light' ? this.setState({selectedTheme: {...lightTheme}}) : this.setState({selectedTheme: {...darkTheme}})
  }

  render() {
    return (
      <React.Fragment>
        <ThemeProvider theme={ this.state.selectedTheme }>

              <Header callback = {this.getTheme}/>
              <BenchmarkDataFilter />    

        </ThemeProvider>
      </React.Fragment>
    );
  }
}

export default App;
