import { createTheme } from "@mui/material/styles";
import { styled } from '@mui/material/styles';
import Card from '@mui/material/Card';
import Switch from '@mui/material/Switch';

export const darkTheme = createTheme({
    palette: {
        //mode: 'dark',
        primary: {
            main: '#082032'
        },
        secondary: {
            main: '#FB9300'
        },
        text: {
            primary: '#fff'
        },
        background: {
            paper: '#334756',
        }
    },
    typography: {
        body1: {
            color: '#fff'
        },
      },
});

export const lightTheme = createTheme({
    palette: {
        //mode: 'light',
        primary: {
            main: '#343F56'
        },
        secondary: {
            main: '#FB9300'
        },
        text: {
            primary: '#000'
        }
    },
    typography: {
        body1: {
             color: '#000'
        },
      },
});

export function toggleTheme(theme){
    if (theme === 'dark')
    {
        document.body.style.backgroundColor = '#2C394B';
        //Card.style.backgroundColor = '#2C394B';

        return {...darkTheme}
    }
    else
    {
        document.body.style.backgroundColor = '#F5E6CA';

        return {...lightTheme}
    }
}

export const CustomCard = styled(Card)(({ theme }) => ({
    color: 'red',
    backgroundColor: 'red'
}));
